import React, { useState } from 'react'
import { FlatList, StyleSheet, Image, Alert } from 'react-native'
import styled from 'styled-components/native';

import headerImage from '../assets/gro..png';
import apple from '../assets/apple.png';
import appleKlyfta from '../assets/appelklyfta.png';
import appleBlom from '../assets/appelblom.png';

import DiaryAdd from './DiaryAdd'
import DiaryFood from './DiaryFood'
import DiaryInput from './DiaryInput'

import { Feather } from '@expo/vector-icons';
import { FontAwesome5 } from '@expo/vector-icons';


const Diary = () => {
    const [food, setFood] = useState([
        // { text: 'Broccoli', key: '1' },
        // { text: 'Morot', key: '2' },
        // { text: 'Ärtor', key: '3' },

    ])

    const pressHandler = (key) => {
        setFood((prevFood) => {
            return prevFood.filter(food => food.key != key);
        })
    }


    const submitHandler = (text) => {

        if (text.length > 2) {
            setFood((prevFood) => {
                return [
                    { text: text, key: Math.random().toString() },
                    ...prevFood
                ];
            })
        } else {
            Alert.alert('Oops!', 'Mer än 2 bokstäver behövs', [
                { text: 'Ok', onPress: () => console.log('alert closed') }
            ]);
        }
    }

    return (
        <>
        <Main>
            <HeaderContainer>
                <Image source={headerImage} style={styles.image} />
            </HeaderContainer>



            <FormContainer>

                <Apple>
                    <Image source={apple} style={styles.imageApple} />
                </Apple>
                <DivAdd>
                    <Rubrik>Dagens Mat</Rubrik>
                    <DiaryAdd submitHandler={submitHandler} />
                </DivAdd>
                <AppleKlyfta>
                <Image source={appleKlyfta} style={styles.imageAppleKlyfta} />
                </AppleKlyfta>
            </FormContainer>

            <ListContainer>
                <List>

                    <FlatList
                        data={food}
                        renderItem={({ item }) => (
                            <DiaryFood item={item} pressHandler={pressHandler} />
                        )}
                    />
                </List>
            </ListContainer>
            <PhotoDiv>
                <PhotoContent>
                <Feather name="camera" size={24} color="white" />
               <Photo>

               </Photo>
                </PhotoContent>
            <AppleBlom>
                    <Image source={appleBlom} style={styles.imageAppleBlom} />
            </AppleBlom>
            </PhotoDiv>
            <CommentContainer>
                <DiaryInput />
            </CommentContainer>


        </Main>
       <Footer>
            <IconDiary>
            <MaterialCommunityIcons name="fountain-pen-tip" size={24} color="black" />
            </IconDiary>
            <IconExplore>

            </IconExplore>
            <IconStatistics>

            </IconStatistics>
            <IconProfile>

            </IconProfile>
       </Footer>
       </>
    )

}

const styles = StyleSheet.create({
    image: {
        height: 70,
        width: 122,
        marginRight: 20
    },
    imageApple: {
        height: 90,
        width: 90,
    },
    imageAppleBlom: {
        height: 100,
        width: 160,
    },
    imageAppleKlyfta: {
        height: 70,
        width: 70,
    }
})

const Main = styled.View`
border: solid red 2px;
    background-color: #BD614E;
    flex: 1;
    align-items: center;
`

const HeaderContainer = styled.View`
width: 100%;
align-items: flex-end;
justify-content: center;
margin-top: 50px;
`

const FormContainer = styled.View`
margin-top: 20px;
width: 100%;
flex-direction:row;
justify-content: space-between;
`
const Apple = styled.View`
justify-content: center; 

`
const AppleKlyfta = styled.View`

`

const DivAdd = styled.View`
padding: 0;
align-items: center;
`

const Rubrik = styled.Text`
color: white;
margin-top: 10px;
font-size: 20px;
`
const ListContainer = styled.View`
width: 100%;
align-items: center;
margin-top: 10px;
`
const PhotoDiv = styled.View`
margin-top: 20px;
width: 100%;
flex-direction:row;
justify-content: space-between;
`
const PhotoContent = styled.View`
flex-direction: row;
margin-left: 60px;
`
const Photo = styled.View`
border: solid white 3px;
border-radius: 10px;
width:120px;
margin-left: 10px;
`

const AppleBlom = styled.View`
align-items: flex-end;
padding: 10px 0 10px 0;
`

const List = styled.View`
border-radius: 20px;
margin-top: 40px;
margin-bottom: 20px;
background-color: white;
padding: 15px 0 15px 10px;
width: 320px;
box-shadow: 0px 0px 8px #6F392E;
`

const CommentContainer = styled.View` 
width: 100%;
align-items: center;
margin-top: 20px;
`
const Footer = styled.View`
margin-top: 0px;
height: 130px;
border: solid white 1px;
width: 100%;
`

const IconDiary = styled.View`

`
const IconExplore = styled.View`

`
const IconStatistics = styled.View`

`

const IconProfile = styled.View`

`


export default Diary;
