import React, { useState, useEffect } from "react";
import { user } from "../reducers/user";
import { useDispatch, useSelector } from "react-redux";
import { FlatList, StyleSheet, Image, Alert, ScrollView, TouchableOpacity, Text } from 'react-native'
import styled from 'styled-components/native';

const URL = "http://localhost:8080/users";
export const LogIn = () => {
  const dispatch = useDispatch();
  const accessToken = useSelector((store) => store.user.login.accessToken);
  const userId = useSelector((store) => store.user.login.userId);
  const statusMessage = useSelector((store) => store.user.login.statusMessage);

  const loginSuccess = (loginResponse) => {};

  const loginFailed = (loginError) => {};

  const logout = () => {};

  const login = () => {
    // Include userId in the path
    fetch(`${URL}/${userId}`, {
      method: "GET",
      // Include the accessToken to get the protected endpoint
      headers: { Authorization: accessToken },
    })
      .then((res) => res.json())
      // SUCCESS: Do something with the information we got back
      .then((json) => loginSuccess(json))
      .catch((err) => loginFailed(err)); //401
  };

  return (
    <Main>
<Header>
      <HeaderText>Profile</HeaderText>
      </Header>
      <Form>
      <FormHeader>Response :</FormHeader>
      <Response>{`${statusMessage}`}</Response>
      </Form>
      {/* <h4>userId :</h4>
