import React, { useState } from 'react';
import styled from 'styled-components/native';
import { Button } from 'react-native'

const HomePage = () => {
    const [name, setName] = useState('Edith')
    const [person, setPerson] =useState({ name: 'Daniel', age: 46});

    const clickHandler = () => {
        setName('Frank');
        setPerson({ name: 'Nathalie', age: 29})
    }

    return (
        <Container>
            <Header>
                <Title>Home Page</Title>
            </Header>
            <Body> 
                <ButtonBox>
                    <ButtonText title='update state' onPress={clickHandler}>My name is <ButtonName>{name}</ButtonName></ButtonText>
                </ButtonBox>
                and my fathers name is {person.name} and he is {person.age}
            </Body>


            <Footer></Footer>
        </Container>
    )

}

const Container = styled.View`
    background-color: white;
`

const Title = styled.Text`
    font-size: 20px;
`

const Header = styled.View`
    background-color: pink;
    padding: 50px;
    width: 100%;
    justify-content: center;
    align-items: center;
`

const Body = styled.View`
    background-color: black;
    width: 100%;
    height:600px;
    color: white;
    justify-content: center;
    align-items: center;
`
const ButtonBox = styled.View`
    background-color: pink;
    justify-content: center;
    align-items: center;  
    width: 200px;
    padding: 10px 0 10px 0;
    border-radius: 50px;
    margin-bottom: 20px;
`

const ButtonText = styled.Text`

`

const ButtonName = styled.Text`
    font-weight: bold;
`


const Footer = styled.View`
    background-color: pink;
    padding: 50px;
    width: 100%;
`



export default HomePage;
