import React, { useState } from 'react'
import styled from 'styled-components/native';
import { TouchableOpacity } from 'react-native'

import { Entypo } from '@expo/vector-icons';

import { useDispatch } from "react-redux";


const FOOD_URL = "http://localhost:8080/food";

const DiaryAdd = ({ submitHandler }) => {
    const [food, setFood] = useState("");
    const [text, setText] = useState('');

    const dispatch = useDispatch();

    const foodHandler = (foodResponse) => {
        dispatch(
            user.actions.setName({
                name: foodResponse.name,
            })
        );
    };

    const handleLoginSuccess = (loginResponse) => {
        dispatch(
          user.actions.setAccessToken({ accessToken: loginResponse.accessToken })
        );
        dispatch(user.actions.setUserId({ userId: loginResponse.userId }));
        dispatch(user.actions.setStatusMessage({ statusMessage: 'Wrong Success' }));

      };

      const handleLoginFailed = (loginError) => {
        dispatch(user.actions.setAccessToken({ accessToken: null }));
        dispatch(user.actions.setStatusMessage({ statusMessage: loginError }));

      };

      const handleSignupSuccess = (signupResponse) => {
        navigation.navigate('LoginForm')
      }



    const handleSignup = (event) => {
        event.preventDefault();

        fetch(FOOD_URL, {
            method: "POST",
            body: JSON.stringify({ food }),
            headers: { "Content-Type": "application/json" },
        })
            .then((res) => res.json())
            .then((json) => foodHandler(json))
            .catch((err) => handleLoginFailed(err));
    };




    return (
        <Main>
            <InputBox>
                <Input
                    placeholderTextColor="white"
                    placeholder='...'
                    maxLength="20"
                    onChangeText={(event) => setFood(event.target.value)}
                />
            </InputBox>
            <TouchableOpacity onPress={() => submitHandler(text)} >
            <TouchableOpacity onPress={handleSignup} >
                <ButtonBox>

                    <ButtonText><Entypo name="heart-outlined" size={40} color="white" /></ButtonText>

                </ButtonBox>
                </TouchableOpacity>
            </TouchableOpacity>
        </Main>
    )

}


const Main = styled.View`
align-items:center;
height: 100px;
`

const Input = styled.TextInput`
font-size: 20px;
color: white;
`
const InputBox = styled.View`
height: 60px;
padding:15px;
margin-top:10px;
border: dotted white 2px;
border-radius:10px;
width: 200px;
margin-bottom: 10px;
box-shadow: 0px 0px 8px #6F392E;
`
const ButtonBox = styled.View`
border-radius: 10px;
width:100px;
padding: 0;
align-items: center;
font-weight:bold;
`
const ButtonText = styled.Text`
color: white;
font-weight:bold;
`

export default DiaryAdd;
