import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { user } from "../reducers/user";
import styled from 'styled-components/native';
import { FlatList, StyleSheet, View, Alert, Image, TouchableOpacity, Text } from 'react-native';

import AnimatedInput from 'react-native-animated-input';

import Photo from '../assets/Dokumentlayout3.jpg';
import LogIn from './LogIn';
import Profile from './Profile'

import { Ionicons } from '@expo/vector-icons';
import { FontAwesome } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { EvilIcons } from '@expo/vector-icons';

const LOGIN_URL = "http://localhost:8080/sessions";
const SIGNUP_URL = "http://localhost:8080/users";

export const LoginForm = ({ navigation }) => {
  const dispatch = useDispatch();
  const accessToken = useSelector((store) => store.user.login.accessToken);
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");

  const [isValid, setIsValid] = useState(true);
  const [names, setNames] = useState(null);
  const [pass, setPass] = useState(null);

  const handleLoginSuccess = (loginResponse) => {
    dispatch(
      user.actions.setAccessToken({ accessToken: loginResponse.accessToken })
    );
    dispatch(user.actions.setUserId({ userId: loginResponse.userId }));
    dispatch(user.actions.setStatusMessage({ statusMessage: 'Fel namnn eller lösenord' }));

  };

  const handleLoginFailed = (loginError) => {
    dispatch(user.actions.setAccessToken({ accessToken: null }));
    dispatch(user.actions.setStatusMessage({ statusMessage: loginError }));

  };

  // To sign up a user.
  const handleSignup = (event) => {
    event.preventDefault();

    fetch(SIGNUP_URL, {
      method: "POST",
      body: JSON.stringify({ name, password }),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => handleLoginSuccess(json))
      .catch((err) => handleLoginFailed(err));
  };

  // To sign up a user.
  const handleLogin = (event) => {
    event.preventDefault();

    fetch(LOGIN_URL, {
      method: "POST",
      body: JSON.stringify({ name, password }),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => handleLoginSuccess(json))
      .catch((err) => handleLoginFailed(err));
  };


      if (!accessToken) {
        // If user is logged out, show login form
        return (
          <Main>


            <Icon>
              <TouchableOpacity onPress={() => navigation.navigate('HomePage')}>
                <Ionicons name="arrow-back-circle-outline" size={50} color="#D2D09D" />
              </TouchableOpacity>
            </Icon>
            <MainContainer>
              <Container>
                <LoginBox>
                  <AppleIcon>
                    <MaterialCommunityIcons name="food-apple-outline" size={40} color="#BD614E" />
                  </AppleIcon>
                  <LoginText>Logga in</LoginText>
                </LoginBox>
                <Form>
                  <FormInput>
                    <Ionicons name="person" size={24} color="#BD614E" />
                    <Input
                      required
                      value={name}
                      placeholder='namn'
                      onChange={(event) => setName(event.target.value)}
                    />
                  </FormInput>
                  <FormInput>
                    <FontAwesome name="key" size={24} color="#BD614E" />
                    <Input
                      value={password}
                      required
                      placeholder='lörsenord'
                      onChange={(event) => setPassword(event.target.value)}
                    />

                  </FormInput>
                  <FormInput>
                    <FontAwesome name="key" size={24} color="#BD614E" />
                    <Input>
                        <AnimatedInput
                        placeholder="Namn"
                        valid={isValid}
                        errorText="Error"
                        onChangeText={text => setNames(text)}
                        value={names}
                        styleLabel={{fontWeight: '600'}}
                        styleBodyContent={styles.bodyContent}
                      />
                      <AnimatedInput
                        placeholder="PasswordLöse"
                        valid={isValid}
                        errorText="Error"
                        onChangeText={text => setPass(text)}
                        value={pass}
                        styleLabel={{fontWeight: '600'}}
                        styleBodyContent={styles.bodyContent}
                      />
                     </Input>
                  </FormInput>
                  <Buttons>
                    <TouchableOpacity onPress={handleLogin} >
                      <View style={styles.button}>

                        <EvilIcons name="arrow-right" size={80} color="#BD614E" />


                      </View>
                    </TouchableOpacity>
                  </Buttons>
                  <LogIn />

                  <Image source={Photo} style={styles.image} />
                </Form>
              </Container>
            </MainContainer>
          </Main>
        );
      } else {
        // If user is logged in, show profile
        return <Profile />;
      }
    };

    const styles = StyleSheet.create({
      image: {
        marginTop: 100,
        width: 400,
        height: 150
      }

    });

    const Main = styled.View`
background-color: white;
flex:1;
`

    const Icon = styled.View`
padding-top: 50px;
margin-left: 20px;
`
    const MainContainer = styled.View`
justify-content: center;
flex:1;
`

    const Container = styled.View`
margin-left: 50px;
margin-right: 50px;
`

    const LoginBox = styled.View`
flex-direction: row;
justify-content:center;
align-items: center;
`

    const AppleIcon = styled.View`
align-items:center;
`

    const LoginText = styled.Text`
font-size: 30px;
color:  #D2D09D; 
`
    const Message = styled.View`
border: solid 1px red;
padding: 10px;

`

    const Form = styled.View`
align-items: center;
margin-top: 30px;
`

    const Input = styled.TextInput`
font-size: 20px;
padding-left: 20px;
`


    const FormInput = styled.View`
flex-direction:row;
border:#D2D09D solid 1px;
border-radius: 50px;
padding: 10px;
width: 100%;
margin-bottom: 20px;

`

    const Buttons = styled.View`
flex-direction: row;
margin-top: 20px;

`




    export default LoginForm;
